"""# Employee info"""class Employee(object):    def __init__(self, id, importance, subordinates):        # It's the unique id of each node.        # unique id of this employee        self.id = id        # the importance value of this employee        self.importance = importance        # the id of direct subordinates        self.subordinates = subordinatesclass Solution(object):    def getImportance(self, employees, id):        dic = {}        ship_dic = {}        dic_list = []        for employee in employees:            dic[employee.id] = employee.importance            ship_dic[employee.id] =  employee.subordinates        if len(ship_dic[id]) == 0:            return  dic[id]        importance_num = dic[id]        dic_list += [id]        while len(dic_list) > 0:                index = dic_list.pop(0)                for i  in ship_dic[index] :                    importance_num += dic[i]                    dic_list.append(i)        return importance_numemployee_list = [Employee(1, 5, [2, 3]), Employee(2, 3, []), Employee(3, 3, []), Employee(4, 5, [5]),                 Employee(5, 3, [])]solution = Solution().getImportance(employee_list, 1)print(solution)