''' 将一个字符串转换成一个整数(实现integer.valueof)的功能,但是string不符合数字要求返回0,数值为0或者字符串不是一个合法的数字返回0'''class Solution:        def  fun1(self,string):             try:                num = int(string)                return num             except ValueError as e:                 return  0        def fun2(self,string):             dic = {'{}'.format(i) : i for i in range(0,10) }             str_list = list(string)             for i in  str_list:                 if  dic.get(i) == None :                     return 0             string = int(''.join(str_list))             return  stringprint(Solution().fun1('as>1'))print(Solution().fun2('12123123'))